def debug(funct=None,message=""):
    def wrapper(funct):
        @wraps(funct)    
        def new_funct(*args,**kw):
            if message:
              print(message,funct.__name__)
            else:
              print(funct.__name__)
            return funct(*args,**kw)
        return new_funct
    if funct:
        return wrapper(funct)
