        print("" if self.a >0 else "-" + "%.1fx"%self.a + "+" if self.b >0 else "-" + "%.1fy = %.1fz"%(self.b, self.c))
        return("{}x + {}y + {}z".format(self.a, self.b, self.c))


    def __str__(self):
        print("%.1fx + %.1fy + %.1fz"%(self.a, self.b, self.c))
        return("{}x + {}y + {}z".format(self.a, self.b, self.c))

    def __add__(self, eq2):
        eqn = LinearEquation()
        eqn.a = self.a + eq2.a
        eqn.b = self.b + eq2.b
        eqn.c = self.c + eq2.c
        return eqn

    def isparallel(self, e2):
        a = (self.a / e2.a) == (self.b / e2.b)
        b = (self.b / e2.b) != (self.c / e2.c)
        if  a and b:
            return True 
        else:
            return False 

    def intersects(self, e2):
        a = (self.a / e2.a) == (self.b / e2.b)
        if a:
            return False 
        else:
            return True

    def overlap(self, e2):
        a = (self.a / e2.a) == (self.b / e2.b)
        b = (self.b / e2.b) == (self.c / e2.c)
