def word_frequency(s,n):
  s = s.lower().replace(',' , '').replace('.' , '').replace('!' , '').replace('?' , '').replace('-' , '')
  words = s.split()
  word_count = {}
  for word in words:
    if word in word_count:
      word_count[word] += 1
    else:
      word_count[word] = 1
  sorted_words = sorted(word_count.items(), key = lambda x: (-x[1], x[0]))
  top_n = {}
  for i in range(n):
    if i < len(sorted_words):
      top_n[sorted_words[i][0]] = sorted_words[i][1]
  return top_n

